font = MOAIFont.new();font:loadFromTTF ( "arial-rounded.ttf", "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789,.?!", 12, 163 )local function init()  fpscounter = MOAITextBox.new()  fpscounter:setFont(font)  fpscounter:setTextSize(24)  fpscounter:setRect(-50, -50, 50, 50)  fpscounter:setLoc(0, 0)  fpscounter:setAlignment(MOAITextBox.LEFT_JUSTIFY)  fpscounter:setYFlip(true)  layer:insertProp(fpscounter)  end  manager = MOAIMetaBallManager.new();    function update(dt)      --fpscounter:setString("FPS: " .. MOAISim.getPerformance())    manager:Update();    manager:Render();endlocal done = false;function testFluidLoop()--  init();  while not done do    update();    coroutine.yield()  endendreturn testFluidLoop